import base64
from pydub import AudioSegment
import io

def convert_mp3_base64_to_linear16_wav_base64(mp3_base64_string: str) -> str:
    """
    Converts a base64 encoded MP3 audio string to a base64 encoded LINEAR16 WAV audio string.
    """
    # Decode the base64 MP3 string
    mp3_data = base64.b64decode(mp3_base64_string)

    # Load the MP3 audio from bytes
    audio = AudioSegment.from_file(io.BytesIO(mp3_data), format="mp3")

    # Convert to LINEAR16 WAV (16-bit, 16kHz, mono) - Google STT recommended format
    # You might need to adjust frame_rate and channels based on your specific STT requirements
    audio = audio.set_frame_rate(16000).set_channels(1).set_sample_width(2)

    # Export to WAV format in memory
    wav_buffer = io.BytesIO()
    audio.export(wav_buffer, format="wav")
    wav_buffer.seek(0)

    # Encode the WAV data to base64
    wav_base64_string = base64.b64encode(wav_buffer.read()).decode('utf-8')

    return wav_base64_string

